import React from "react";
import "../css/ChapterSelection.css";
import {Link} from "react-router-dom";

class ChapterSelection extends React.Component {
  constructor(props) {
    super(props)
  }
  render() {
    return (
      <div>
        <h1>Chapter selection</h1>
        <div id="chapters">
          <Link className="chapter" id="chapter-1" to="/the-protesters-handbook">
            <div className="image-placeholder"></div>
            The protester's handbook
          </Link>
          <div id="chapters-bottom-row">
            <Link className="chapter" id="chapter-2" to="/the-revolution-is-female">
              <div className="image-placeholder"></div>
              The revolution is female
            </Link>
            <a className="chapter disabled-link" id="chapter-3">
              <div className="image-placeholder"></div>
              Cacerolazo
            </a>
          </div>
          <div className="controls">
            <Link className="control-link" to="/prologue">&lt; Back to prologue</Link>
            <Link className="control-link" to="/">Skip to epilogue &gt;</Link>
          </div>
        </div>
      </div>
    );
  }
}

export default ChapterSelection;
