import React from "react";
import "../css/Prologue.css";
import prologue from "../media/prologue.mp4";
import {Link} from "react-router-dom";
import $ from "jquery";

class Controls extends React.Component {
  constructor(props) {
    super(props);
  }
  render() {
    if (this.props.scene == 0) {
      return (
        <div className="controls">
          <Link className="control-link" to="/">&lt; Back</Link>
          <a className="control-link" onClick={() => this.props.setScene(1)}>Next &gt;</a>
        </div>
      )
    }
    else {
      return (
        <div className="controls">
          <a className="control-link" onClick={() => this.props.setScene(this.props.scene - 1)}>&lt; Back</a>
          <a className="control-link" onClick={() => this.props.setScene(this.props.scene + 1)}>Next &gt;</a>
        </div>
      )
    }
  }
}

class Scene extends React.Component {
  constructor(props) {
    super(props);
  }
  render() {
    switch (this.props.scene) {
      case 0:
        return (<SceneZero />);
        break;
      case 1:
        return (<SceneOne />);
      default:
        return (<SceneZero />);
    }
  }
}

class SceneZero extends React.Component {
  constructor(props) {
    super(props);
  }
  play() {
    $("video")[0].play();
  }
  render() {
    return (
      <div>
        <div className="video-container">
          <video>
            <source src={prologue} type="video/mp4" />
          </video>
        </div>
        <button className="play-button" onClick={this.play}>Play</button>
      </div>
    );
  }
}

function SceneOne() {
  return (
    <h1>SDKHHKHKHKHKHKHKHSuuuuu</h1>
  );
}

class Prologue extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      scene: 0
    }
    this.setScene = this.setScene.bind(this);
  }
  setScene(i) {
    this.setState({
      scene: i
    })
  }
  render() {
    return (
      <div>
        <Scene scene={this.state.scene} />
        <Controls scene={this.state.scene} setScene={this.setScene} />
      </div>
    );
  }
}
export default Prologue;
