import React from "react";
import mapboxgl from "mapbox-gl";

mapboxgl.accessToken = 'pk.eyJ1IjoiY2hhcmxlc2luZ2VhIiwiYSI6ImNqcWpudGdsMDBwdmY0OXBvanQyaHN0MnoifQ._0O-n_18aXTVaue93HD6PQ';

class SceneTwo extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      lng: 0,
      lat: 25,
      zoom: 1.5
    };
  }
  componentDidMount() {
    const map = new mapboxgl.Map({
      container: this.mapContainer,
      style: "mapbox://styles/mapbox/dark-v10",
      center: [this.state.lng, this.state.lat],
      zoom: this.state.zoom,
      logoPosition: "top-right",
      renderWorldCopies: false,
      interactive: false,
      attributionControl: false
    });
    const attribution = new mapboxgl.AttributionControl({
      compact: true
    });
    attribution.setPosition("topleft");
    map.addControl(attribution);
}
  render() {
    return (
      <div>
        <div ref={el => this.mapContainer = el} className="mapContainer" />
      </div>
    );
  }
}

export default SceneTwo;
