import React from "react";
import { Tweet } from 'react-twitter-widgets';
import economist from "../../media/prologue/logo-economist.png";

class Map extends React.Component {
  constructor(props) {
    super(props);
  }
  render() {
    return (
      <div id="map"></div>
    );
  }
}

class Articles extends React.Component {
  constructor(props) {
    super(props);
  }
  render() {
    return (
      <div id="articles">
        <div>
          <img src="economist" alt="The Economist" />
          <h3>Digital media fuel global protests but can be used against them</h3>
          <h6>Protesters in 2019 relied heavily on digital media for organisation, publicity and communication.</h6>
        </div>
        <div>
          <img src="" alt="" />
          <h3>2019: the year of street protest</h3>
          <h6>Social media is a powerful organising tool everywhere â€” allowing protesters to crowdsource grievances, slogans and tactics.</h6>
        </div>
      </div>
    );
  }
}

class SceneThree extends React.Component {
  constructor(props) {
    super(props)
  }
  render() {
    return (
      <div id="scene-three">
        <Map />
        <Articles />
      </div>
    );
  }
}

export default SceneThree;
