import React from "react";
import "../css/Prologue.css";
import prologue from "../media/prologue.mp4";
import {Link} from "react-router-dom";

class Controls extends React.Component {
  constructor(props) {
    super(props);
  }
  render() {
    if (this.props.scene == 0) {
      return (
        <div className="controls">
          <Link className="control-link" to="/">&lt; Back</Link>
          <Link className="control-link" to="/">Next &gt;</Link>
        </div>
      )
    }
  }
}

function SceneZero() {
  return (
    <div className="video-container">
      <video autoPlay muted>
        <source src={prologue} type="video/mp4" />
      </video>
    </div>
  );
}

class Prologue extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      scene: 0
    }
  }
  render() {
    return (
      <div>
        <SceneZero />
        <Controls scene={this.state.scene} />
      </div>
    );
  }
}
export default Prologue;
