import React from "react";
import $ from "jquery";
import { Tweet } from 'react-twitter-widgets';
import economist from "../../media/prologue/logo-economist.png";
import ft from "../../media/prologue/logo-ft.png";
import npr from "../../media/prologue/logo-npr.png";
import ny from "../../media/prologue/logo-new-yorker.png";

class Map extends React.Component {
  constructor(props) {
    super(props);
  }
  render() {
    return (
      <div id="map"></div>
    );
  }
}

class Articles extends React.Component {
  constructor(props) {
    super(props);
  }
  // componentDidMount() {
  //   $(".article-highlight").each((i, article) => {
  //     var width = 100 - (100 * article.getBoundingClientRect().top / window.innerHeight);
  //     article.style.width = Math.min(100, width) + "%";
  //   });
  //   $(document).scroll(function() {
  //     $(".article-highlight").each((i, article) => {
  //       var width = 100 - (100 * article.getBoundingClientRect().top / window.innerHeight);
  //       article.style.width = Math.min(100, width) + "%";
  //     })
  //   });
  // }
  render() {
    return (
      <div id="articles">
        <div className="article article-1">
          <a target="_blank" href="https://www.economist.com/international/2021/01/16/digital-media-fuel-global-protests-but-can-be-used-against-them">
            <img src={economist} alt="The Economist" />
            <h1>Digital media fuel global protests but can be used against them</h1>
          </a>
          <h2><span className="highlight" data-text="Protesters in 2019 relied heavily on digital media for organisation, publicity and communication.">
          Protesters in 2019 relied heavily on digital media for organisation, publicity and communication.
          </span></h2>
          <div className="article-highlight"></div>
        </div>
        <div className="article article-3">
          <a target="_blank" href="https://www.ft.com/content/9f7e94c4-2563-11ea-9a4f-963f0ec7e134">
            <img src={ft} alt="The Financial Times" />
            <h1>2019: the year of street protest</h1>
          </a>
          <div className="article-highlight"></div>
          <h2>Social media is a powerful organising tool everywhere — allowing protesters to crowdsource grievances, slogans and tactics.</h2>
        </div>
        <div className="article article-2">
          <a target="_blank" href="https://www.npr.org/2020/03/09/809684634/lebanons-government-is-accused-of-swarming-whatsapp-to-catch-protesters?t=1613410699449">
            <img src={npr} alt="NPR" />
            <h1>Lebanon's Government Is Accused Of Swarming WhatsApp To Catch Protesters</h1>
          </a>
          <div className="article-highlight"></div>
          <h2>They organized and shared updates on WhatsApp, streamed protests live on Twitter and highlighted police abuse on Instagram.</h2>
        </div>
        <div className="article article-1">
          <a target="_blank" href="https://www.newyorker.com/culture/cultural-comment/the-second-act-of-social-media-activism">
            <img src={ny} alt="The New Yorker" />
            <h1>The Second Act of Social-Media Activism</h1>
          </a>
          <h2>Protest guides, generated from years of on-the-ground activist experience, are readily shared over Twitter and Instagram, telling readers how to blur faces in photographs or aid in de-arrests.</h2>
        </div>
        <div className="article article-3">
          <a target="_blank" href="https://www.ft.com/content/19dc5dfe-f67b-11e9-a79c-bc9acae3b654">
            <img src={ft} alt="The Financial Times" />
            <h1>Leaderless rebellion: how social media enables global protests</h1>
          </a>
          <h2>Social media also allows a movement in one place to take inspiration from news of revolts in another. The occupation of the airport in Barcelona last week was a tactic borrowed from Hong Kong. The Sudanese and Algerian uprisings this year borrowed each other’s imagery and slogans.</h2>
        </div>
        <div className="article article-1">
          <a target="_blank" href="https://www.newyorker.com/news/our-columnists/the-story-of-2019-protests-in-every-corner-of-the-globe">
            <img src={ny} alt="The New Yorker" />
            <h1>The Story of 2019: Protests in Every Corner of the Globe</h1>
          </a>
          <h2>There may be a global contagion due to social media. Seeing protests in other places motivates people to be willing to go to the streets in their own countries.</h2>
        </div>
        <div className="article"></div>
        <div className="article"></div>
        <div className="article"></div>
        <div className="article"></div>
        <div className="article"></div>
        <div className="article"></div>
        <div className="article"></div>
        <div className="article"></div>
        <div className="article"></div>
        <div className="article"></div>
        <div className="article"></div>
        <div className="article"></div>
        <div className="article"></div>
        <div className="article"></div>
        <div className="article"></div>
      </div>
    );
  }
}

class SceneThree extends React.Component {
  constructor(props) {
    super(props)
  }
  render() {
    return (
      <div id="scene-three">
        <Map />
        <Articles />
      </div>
    );
  }
}

export default SceneThree;
